mapper.insert:
INSERT INTO QNA_REPLY(
                    BNO
                  , RNO
                  , CONTENT
                  , WRITER
                             )
           VALUES(    #{bno}
                    , QNA_REPLY_SEQ.NEXTVAL
                    , #{content}
                    , #{writer}   )

mapper.select:
select bno
from QNA_REPLY
where rno = #{rno}
